    ]

    const [products, setProducts] = useState([])

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await axios.get(process.env.REACT_APP_API_URL + "/products", {
                    headers: { Authorization: "Bearer " + process.env.REACT_APP_API_TOKEN },
