                console.log(data);
            } catch (err) {
                console.log(err)
            }
        }
        fetchData();
    }, [])

    return (
        <div className="featuredProducts">
            <div className="top">
                <h1>{type} products</h1>
            </div>

            <div className="bottom">
                {data.map(item => (
                    <Card item={item} key={item.id} />
                ))}
            </div>
        </div>
    );
}

export default FeaturedProducts;
